package common;

import libepg.epg.section.Section;
import org.apache.commons.codec.DecoderException;
import org.apache.commons.codec.binary.Hex;

/**
 * テスト用のSDTデータ
 *
 * @author normal
 */
public final class TestSection {

    public static final String TEST_SDT_1 = "";

    public static final String TEST_SDT_2 = "";

    public static final String TEST_SDT_3_CRCERROR = "";

    public static final String TEST_EIT_1 = "";

    private static Section getSdt(String sdtHexDump) throws DecoderException {
        char[] xc = sdtHexDump.toCharArray();
        byte[] xb = Hex.decodeHex(xc);
        return new Section(xb);
    }

    public static Section getSdt1() throws DecoderException {
        return getSdt(TestSection.TEST_SDT_1);
    }

    public static Section getSdt2() throws DecoderException {
        return getSdt(TestSection.TEST_SDT_2);
    }

    public static Section getSdt3_CRCERROR() throws DecoderException {
        return getSdt(TestSection.TEST_SDT_3_CRCERROR);
    }

    public static Section getEit1() throws DecoderException {
        return getSdt(TestSection.TEST_EIT_1);
    }

    private TestSection() {
    }
}
